import React, { useState } from "react";
import "./Grid.css";

const generateRandomNumber = (min, max) => {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min + 1)) + min;
};

const [totalRows, totalCols] = [20, 40];

var sourceRow = 10;
var sourceCol = 10;
var destinationRow = 10;
var destinationCol = 30;

var grid = [];
var weight = [];

class Vertex {
    constructor(x, y) {
        this.row = x;
        this.col = y;
        this.isWall = false;
        this.isSource = x === sourceRow && y === sourceCol;
        this.isDestination = x === destinationRow && y === destinationCol;
        this.weight = weight[x][y];
    }
}

const initializeWeights = () => {
    for (let row = 0; row < totalRows; ++row) {
        let weightOfColumns = [];
        for (let col = 0; col < totalCols; ++col) {
            let weight = generateRandomNumber(10, 30);
            weightOfColumns.push(weight);
        }
        weight.push(weightOfColumns);
    }
};

initializeWeights();

const createVertices = () => {
    for (let row = 0; row < totalRows; ++row) {
        let columns = [];
        for (let col = 0; col < totalCols; ++col) {
            columns.push(new Vertex(row, col));
        }
        grid.push(columns);
    }
};

createVertices();

const getNodeClass = (isSource, isDestination, isWall) => {
    let nodeType = "node";
    if (isSource) {
        nodeType += " source-node";
    } else if (isDestination) {
        nodeType += " destination-node";
    } else if (isWall) {
        nodeType += " wall-node";
    }
    return nodeType;
};

var isMouseDown = false;
var isRunning = false;

const handleMouseDown = (a, b) => {
    console.log(a + " " + b);
    // if (column.isSource === false && column.isDestination === false) {
    //     isMouseDown = true;
    // }
};

const handleMouseUp = (a, b) => {
    console.log(a + " " + b);
    // if (column.isSource === false && column.isDestination === false) {
    //     isMouseDown = false;
    // }
};

const Grid = () => {
    const [initialGrid, setGrid] = useState(grid);
    return (
        <div className="board">
            <div className="full-grid">
                {initialGrid.map((row, rowIndex) => {
                    return (
                        <div key={rowIndex} className="row">
                            {row.map((column, columnIndex) => {
                                return (
                                    <div
                                        className={getNodeClass(
                                            column.isSource,
                                            column.isDestination,
                                            column.isWall
                                        )}
                                        id={`${rowIndex}-${columnIndex}`}
                                        key={columnIndex}
                                        row={column.row}
                                        col={column.col}
                                        nodeType={getNodeClass(
                                            column.isSource,
                                            column.isDestination,
                                            column.isWall
                                        )}
                                        nodeWeight={column.weight}
                                        isSource={column.isSource}
                                        isDestination={column.isDestination}
                                        isWall={column.isWall}
                                        onMouseUp={handleMouseUp(
                                            column.isSource,
                                            column.isDestination
                                        )}
                                        onMouseDown={handleMouseDown(
                                            column.isSource,
                                            column.isDestination
                                        )}
                                        onMouseEnter={() => {
                                            if (isMouseDown) {
                                                if (
                                                    column.isSource === false &&
                                                    column.isDestination ===
                                                        false
                                                ) {
                                                    if (
                                                        column.isWall === false
                                                    ) {
                                                        column.isWall = true;
                                                        document.getElementById(
                                                            `${rowIndex}-${columnIndex}`
                                                        ).className =
                                                            "node wall-node";
                                                        setGrid(initialGrid);
                                                    } else {
                                                        column.isWall = false;
                                                        document.getElementById(
                                                            `${rowIndex}-${columnIndex}`
                                                        ).className = "node";
                                                        setGrid(initialGrid);
                                                    }
                                                }
                                            }
                                        }}
                                    >
                                        <p className="not-selectable">
                                            {column.weight}
                                        </p>
                                    </div>
                                );
                            })}
                        </div>
                    );
                })}
            </div>
            <div className="shortest-path-result not-selectable">
                <p>Shortest path has not been found yet !!!</p>
            </div>
        </div>
    );
};

export default Grid;
