import "./Tutorial.css";

const iconImage = require("./media/icon.png");

const sourceImage = require("./media/source.png");
const destinationImage = require("./media/destination.png");
const wallImage = require("./media/wall.png");

const createWalls = require("./media/createWalls.gif");

const Tutorial = (props) => {
    if (props.modalID === 1) {
        return (
            <div>
                <h1 className="welcome">Welcome to Shortest Path Visualizer</h1>
                <h3>
                    This short guide is an user-manual for all the features in
                    this application.
                </h3>
                <p className="modal1">
                    It is strongly recommended to go thorugh this tutorial. But
                    feel free to skip.
                </p>
                <img src={iconImage} className="app-icon" />
            </div>
        );
    } else if (props.modalID === 2) {
        return (
            <div>
                <h2 className="modal2">What does the images represent ?</h2>
                <div className="node-icon-div">
                    <p className="modal2-p">
                        Source:
                        <img src={sourceImage} className="node-icons-1" />
                    </p>
                    <p className="modal2-p">
                        Destination:&nbsp;&nbsp;{" "}
                        <img src={destinationImage} className="node-icons-2" />
                        &nbsp;&nbsp;&nbsp;&nbsp;{" "}
                    </p>
                    <p className="modal2-p">
                        Walls:&nbsp;&nbsp;{" "}
                        <img src={wallImage} className="node-icons-3" />
                    </p>
                </div>
            </div>
        );
    } else if (props.modalID === 3) {
        return (
            <div>
                <h2 className="modal3">How to create walls ?</h2>
                <p className="modal3-p">
                    To create walls, do this, that and the other
                </p>
                <img src={createWalls} />
            </div>
        );
    } else {
        return (
            <div>
                <h1>hello 3</h1>
            </div>
        );
    }
};

export default Tutorial;
