import React, { useState } from "react";
import "./Grid.css";

const generateRandomNumber = (min, max) => {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min + 1)) + min;
};

const [totalRows, totalCols] = [20, 40];

var sourceRow = 10;
var sourceCol = 10;
var destinationRow = 10;
var destinationCol = 30;

var grid = [];
var weight = [];

class Vertex {
    constructor(x, y) {
        this.row = x;
        this.col = y;
        this.isWall = false;
        this.isSource = x === sourceRow && y === sourceCol;
        this.isDestination = x === destinationRow && y === destinationCol;
        this.weight = weight[x][y];
    }
}

const initializeWeights = () => {
    for (let row = 0; row < totalRows; ++row) {
        let weightOfColumns = [];
        for (let col = 0; col < totalCols; ++col) {
            let weight = generateRandomNumber(10, 30);
            weightOfColumns.push(weight);
        }
        weight.push(weightOfColumns);
    }
};

initializeWeights();

var previousSource;
var previousDestination;

const createVertices = () => {
    for (let row = 0; row < totalRows; ++row) {
        let columns = [];
        for (let col = 0; col < totalCols; ++col) {
            columns.push(new Vertex(row, col));
        }
        grid.push(columns);
    }
    previousSource = grid[sourceRow][sourceCol];
    previousDestination = grid[destinationRow][destinationCol];
};

createVertices();

const getNodeClass = (node) => {
    let nodeType = "node";
    if (node.isSource === true) {
        nodeType += " source-node";
    } else if (node.isDestination === true) {
        nodeType += " destination-node";
    } else if (node.isWall === true) {
        nodeType += " wall-node";
    }
    return nodeType;
};

var isMouseDown = false;
var isRunning = false;
var isSourceSelected = false;
var isDestinationSelected = false;

const handleMouseUp = (node) => {
    if (node.isSource === false && node.isDestination === false) {
        isMouseDown = false;
    } else {
        if (isSourceSelected === true) isSourceSelected = false;
        if (isDestinationSelected === true) isDestinationSelected = false;
        isMouseDown = false;
    }
};

const handleMouseDown = (node) => {
    if (isSourceSelected === false && isDestinationSelected === false) {
        if (node.isSource === false && node.isDestination === false) {
            isMouseDown = true;
        } else if (node.isSource === true) {
            isSourceSelected = true;
            isMouseDown = true;
        } else if (node.isDestination === true) {
            isDestinationSelected = true;
            isMouseDown = true;
        }
    } else if (isSourceSelected === true) {
        //
    } else if (isDestinationSelected === true) {
        //
    }
};

const handleMouseEnter = (node) => {
    if (isMouseDown === true) {
        if (isSourceSelected === false && isDestinationSelected === false) {
            if (node.isSource === false && node.isDestination === false) {
                if (node.isWall === false) {
                    node.isWall = true;
                    document.getElementById(
                        `${node.row}-${node.col}`
                    ).className = "node wall-node";
                } else {
                    node.isWall = false;
                    document.getElementById(
                        `${node.row}-${node.col}`
                    ).className = "node";
                }
            }
        } else if (isSourceSelected === true) {
            let currentNodeClassName = getNodeClass(node);
            previousSource = { ...node };
            document.getElementById(
                `${previousSource.row}-${previousSource.col}`
            ).className = currentNodeClassName;
            node.isSource = true;
            node.isWall = false;
            document.getElementById(`${node.row}-${node.col}`).className =
                getNodeClass(node);
            previousSource = node;
        } else if (isDestinationSelected === true) {
            // var previousDestination = { ...node };
            // document.getElementById(
            //     `${previousDestination.row}-${previousDestination.col}`
            // ).className = getNodeClass(previousSource);
            // node.isDestination = true;
            // node.isWall = false;
            // document.getElementById(`${node.row}-${node.col}`).className =
            //     getNodeClass(node);
        }
    }
};

const Grid = () => {
    const [initialGrid, setGrid] = useState(grid);
    return (
        <div className="board">
            <div className="full-grid">
                {initialGrid.map((row, rowIndex) => {
                    return (
                        <div key={rowIndex} className="row">
                            {row.map((column, columnIndex) => {
                                return (
                                    <div
                                        className={getNodeClass(column)}
                                        id={`${rowIndex}-${columnIndex}`}
                                        key={columnIndex}
                                        row={column.row}
                                        col={column.col}
                                        nodeWeight={column.weight}
                                        isSource={column.isSource}
                                        isDestination={column.isDestination}
                                        isWall={column.isWall}
                                        onMouseUp={() => {
                                            handleMouseUp(column);
                                            setGrid(initialGrid);
                                        }}
                                        onMouseDown={() => {
                                            handleMouseDown(column);
                                            setGrid(initialGrid);
                                        }}
                                        onMouseEnter={() => {
                                            handleMouseEnter(column);
                                            setGrid(initialGrid);
                                        }}
                                    >
                                        <p className="not-selectable">
                                            {column.weight}
                                        </p>
                                    </div>
                                );
                            })}
                        </div>
                    );
                })}
            </div>
            <div className="shortest-path-result not-selectable">
                <p>Shortest path has not been found yet !!!</p>
            </div>
        </div>
    );
};

export default Grid;
