import Queue from "../data-structures/Queue.js";
const [totalRows, totalCols] = [20, 30];

const dx = [1, -1, 0, 0];
const dy = [0, 0, 1, -1];

const BFS = (grid, src, dst) => {
    console.log(src.row, src.col);
    console.log(dst.row, dst.col);
    let distance = [];
    for (let row = 0; row < totalRows; ++row) {
        let d = [];
        for (let col = 0; col < totalCols; ++col) {
            d.push(Infinity);
        }
        distance.push(d);
    }
    distance[src.row][src.col] = 0;
    let q = new Queue();
    q.enqueue(src);
    while (q.empty() === false) {
        let curr = q.dequeue();
        if (curr.isDestination === true) return distance[curr.row][curr.col];
        for (let dir = 0; dir < 4; ++dir) {
            let r = curr.row + dx[dir];
            let c = curr.col + dy[dir];
            if (r >= 0 && c >= 0 && r < totalRows && c < totalCols) {
                let adj = grid[r][c];
                if (adj.isWall === false) {
                    if (adj.isVisited === false) {
                        distance[adj.row][adj.col] =
                            distance[curr.row][curr.col] + 1;
                        adj.isVisited = true;
                        q.enqueue(adj);
                    }
                }
            }
        }
    }
    return [distance[dst.row][dst.col], grid];
};

export default BFS;
