import React, { useState } from "react";
import "./Grid.css";

const generateRandomNumber = (min, max) => {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min + 1)) + min;
};

const [totalRows, totalCols] = [20, 40];

var sourceRow = 10;
var sourceCol = 10;
var destinationRow = 10;
var destinationCol = 30;

var grid = [];
var weight = [];

class Node {
    constructor(x, y) {
        this.row = x;
        this.col = y;
        this.isWall = false;
        this.isSource = x === sourceRow && y === sourceCol;
        this.isDestination = x === destinationRow && y === destinationCol;
        this.weight = weight[x][y];
    }
}

const initializeWeights = () => {
    for (let row = 0; row < totalRows; ++row) {
        let weightOfColumns = [];
        for (let col = 0; col < totalCols; ++col) {
            let weight = generateRandomNumber(10, 30);
            weightOfColumns.push(weight);
        }
        weight.push(weightOfColumns);
    }
};

initializeWeights();

var previousSource;
var previousDestination;

const createVertices = () => {
    for (let row = 0; row < totalRows; ++row) {
        let columns = [];
        for (let col = 0; col < totalCols; ++col) {
            columns.push(new Node(row, col));
        }
        grid.push(columns);
    }
    previousSource = grid[sourceRow][sourceCol];
    previousDestination = grid[destinationRow][destinationCol];
};

createVertices();

const getNodeClass = (node) => {
    let nodeType = "node";
    if (node.isSource === true) {
        nodeType += " source-node";
    } else if (node.isDestination === true) {
        nodeType += " destination-node";
    } else if (node.isWall === true) {
        nodeType += " wall-node";
    }
    return nodeType;
};

var classNameBeforeSource = "node";
var classNameBeforDestination = "node";

var isMouseDown = false;
// var isRunning = false;
var isSourceSelected = false;
var isDestinationSelected = false;

const handleMouseUp = (node) => {
    if (node.isSource === false && node.isDestination === false) {
        isMouseDown = false;
    } else {
        if (isSourceSelected === true) isSourceSelected = false;
        if (isDestinationSelected === true) isDestinationSelected = false;
        isMouseDown = false;
    }
};

const handleMouseDown = (node) => {
    if (isSourceSelected === false && isDestinationSelected === false) {
        if (node.isSource === false && node.isDestination === false) {
            isMouseDown = true;
        } else if (node.isSource === true) {
            isSourceSelected = true;
            isMouseDown = true;
        } else if (node.isDestination === true) {
            isDestinationSelected = true;
            isMouseDown = true;
        }
    } else if (isSourceSelected === true) {
        //
    } else if (isDestinationSelected === true) {
        //
    }
};

const handleMouseEnter = (node) => {
    if (isMouseDown === true) {
        if (isSourceSelected === false && isDestinationSelected === false) {
            if (node.isSource === false && node.isDestination === false) {
                if (node.isWall === false) {
                    node.isWall = true;
                    document.getElementById(
                        `${node.row}-${node.col}`
                    ).className = "node wall-node";
                } else {
                    node.isWall = false;
                    document.getElementById(
                        `${node.row}-${node.col}`
                    ).className = "node";
                }
            }
        } else if (isSourceSelected === true) {
            if (node.isDestination === false) {
                let currentNodeClassName = getNodeClass(node);
                previousSource.isSource = node.isSource;
                previousSource.isDestination = node.isDestination;
                previousSource.isWall = node.isWall;
                document.getElementById(
                    `${previousSource.row}-${previousSource.col}`
                ).className = classNameBeforeSource;
                node.isSource = true;
                node.isWall = false;
                document.getElementById(`${node.row}-${node.col}`).className =
                    getNodeClass(node);
                previousSource = node;
                classNameBeforeSource = currentNodeClassName;
            }
        } else if (isDestinationSelected === true) {
            if (node.isSource === false) {
                let currentNodeClassName = getNodeClass(node);
                previousDestination.isSource = node.isSource;
                previousDestination.isDestination = node.isDestination;
                previousDestination.isWall = node.isWall;
                document.getElementById(
                    `${previousDestination.row}-${previousDestination.col}`
                ).className = classNameBeforDestination;
                node.isDestination = true;
                node.isWall = false;
                document.getElementById(`${node.row}-${node.col}`).className =
                    getNodeClass(node);
                previousDestination = node;
                classNameBeforDestination = currentNodeClassName;
            }
        }
    }
};

const Grid = () => {
    const [initialGrid, setGrid] = useState(grid);
    return (
        <div className="board">
            <div className="navbar justify-content-center app-header">
                <p className="app-name">Shortest Path Visualizer</p>
            </div>

            <div className="navbar justify-content-center complete-navbar">
                <ul class="nav justify-content-center navbar-content">
                    <li class="nav-item dropdown">
                        <a
                            class="nav-link dropdown-toggle"
                            role="button"
                            data-bs-toggle="dropdown"
                            aria-expanded="false"
                            href="#"
                        >
                            Select Algorithm
                        </a>
                        <ul class="dropdown-menu dropdown-menu-dark">
                            <li>
                                <a class="dropdown-item" href="#">
                                    Action
                                </a>
                            </li>
                            <li>
                                <a class="dropdown-item" href="#">
                                    Another action
                                </a>
                            </li>
                            <li>
                                <a class="dropdown-item" href="#">
                                    Something else here
                                </a>
                            </li>
                        </ul>
                    </li>
                    <li class="nav-item">
                        <a class="nav-link" href="#">
                            Visualize
                        </a>
                    </li>
                    <li class="nav-item">
                        <button type="button" class="btn">
                            Clear Walls
                        </button>
                    </li>
                    <li class="nav-item">
                        <button type="button" class="btn">
                            Clear Grid
                        </button>
                    </li>
                    <li class="nav-item">
                        <button type="button" class="btn">
                            Reconfigure Grid
                        </button>
                    </li>
                </ul>
            </div>

            {/* Visualizer */}
            <div className="full-grid">
                {initialGrid.map((row, rowIndex) => {
                    return (
                        <div key={rowIndex} className="rows">
                            {row.map((column, columnIndex) => {
                                return (
                                    <div
                                        className={getNodeClass(column)}
                                        id={`${rowIndex}-${columnIndex}`}
                                        key={columnIndex}
                                        onMouseUp={() => {
                                            handleMouseUp(column);
                                            setGrid(initialGrid);
                                        }}
                                        onMouseDown={() => {
                                            handleMouseDown(column);
                                            setGrid(initialGrid);
                                        }}
                                        onMouseEnter={() => {
                                            handleMouseEnter(column);
                                            setGrid(initialGrid);
                                        }}
                                    >
                                        <p className="not-selectable weight-value">
                                            {column.weight}
                                        </p>
                                    </div>
                                );
                            })}
                        </div>
                    );
                })}
            </div>
            <div className="shortest-path-result not-selectable">
                <p>Shortest path has not been found yet !!!</p>
            </div>
        </div>
    );
};

export default Grid;
