import "./Grid.css";
import Node from "./node/Node";

const generateRandomNumber = (min, max) => {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min + 1)) + min;
};

const [totalRows, totalCols] = [20, 20];

var sourceRow = 10;
var sourceCol = 5;
var destinationRow = 10;
var destinationCol = 15;

var grid = Array.from(Array(totalRows), () => new Array(totalCols));

const addWeights = () => {
    for (let row = 0; row < totalRows; ++row) {
        for (let col = 0; col < totalCols; ++col) {
            grid[row][col] = generateRandomNumber(10, 30);
        }
    }
};

const Grid = (props) => {
    return (
        <div className="full-grid">
            {grid.map((row, row_index) => {
                return (
                    <div className="row">
                        {row.map((column, column_index) => {
                            return <Node nodeType="node" />;
                        })}
                    </div>
                );
            })}
            ;
        </div>
    );
};

export default Grid;
