import "./Grid.css";
import Node from "./node/Node";

const generateRandomNumber = (min, max) => {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min + 1)) + min;
};

const [totalRows, totalCols] = [20, 40];

var sourceRow = 10;
var sourceCol = 5;
var destinationRow = 10;
var destinationCol = 15;

var grid = Array.from(Array(totalRows), () => new Array(totalCols));
var weights = Array.from(Array(totalRows), () => new Array(totalCols));

const addWeights = () => {
    for (let row = 0; row < totalRows; ++row) {
        for (let col = 0; col < totalCols; ++col) {
            weights[row][col] = generateRandomNumber(10, 30);
            grid[row][col] = weights[row][col];
        }
    }
};

addWeights();

const getNodeClass = (rowIndex, columnIndex) => {
    const nodeType = "node";
    if (rowIndex == sourceRow && columnIndex == sourceCol) {
        nodeType += " source-node";
    }
    if (rowIndex == destinationRow && columnIndex == destinationCol) {
        nodeType += " destination-node";
    }
    return nodeType;
};

const Grid = () => {
    return (
        <div className="board">
            <div className="full-grid">
                {grid.map((row, rowIndex) => {
                    return (
                        <div key={rowIndex} className="row">
                            {row.map((column, columnIndex) => {
                                return (
                                    <Node
                                        key={columnIndex}
                                        nodeType={getNodeClass(
                                            rowIndex,
                                            columnIndex
                                        )}
                                        nodeWeight={
                                            weights[rowIndex][columnIndex]
                                        }
                                    />
                                );
                            })}
                        </div>
                    );
                })}
            </div>
            <div className="shortest-path-result">
                <p>Shortest path is impossible</p>
            </div>
        </div>
    );
};

export default Grid;
