import { useState } from "react";
import "./Node.css";

var isMouseDown = false;

const Node = (props) => {
    const [initialMouseState, setMouseState] = useState(isMouseDown);
    const setMouseUp = () => {
        console.log("Mouse Up");
        setMouseState(false);
    };
    const setMouseDown = () => {
        console.log("Mouse Down");
        setMouseState(true);
    };
    const nodeClass = props.nodeType;
    const nodeWeight = props.nodeWeight;
    const isSource = props.isSource;
    const isDestination = props.isDestination;
    const isWall = props.isWall;
    const row = props.row;
    const col = props.col;

    const clickChecker = () => {
        console.log(`${row}, ${col}`);
    };

    return (
        <div
            className={nodeClass}
            onMouseUp={setMouseUp}
            onMouseDown={setMouseDown}
        >
            <p>{nodeWeight}</p>
        </div>
    );
};

export default Node;
